mapper.select:
select ecc.* from tbl_employee_contribution_coefficient ecc , tbl_employee_info tei where ecc.employee_id = tei.EMPLOYEE_ID
    and ecc.employee_id = #{employeeId:VARCHAR}  
    and tei.employee_name = #{employeeName:VARCHAR}

select ecc.* from tbl_employee_contribution_coefficient ecc ,tbl_employee_info tei where ecc.employee_id = tei.EMPLOYEE_ID
    and ecc.employee_id = #{employeeId:VARCHAR}  
    and ecc.employee_name = #{employeeName:VARCHAR}

select count(1) from tbl_employee_contribution_coefficient ecc ,tbl_employee_info tei where ecc.employee_id = tei.EMPLOYEE_ID
    and ecc.employee_id = #{employeeId:VARCHAR}  
    and ecc.employee_name = #{employeeName:VARCHAR}

mapper.update:
update tbl_employee_contribution_coefficient 

      contribution_coefficient = #{contributionCoefficient:VARCHAR} , 


      and employee_id = #{employeeId:VARCHAR}  
      and employee_income_id = #{employeeIncomeId:VARCHAR}
