mapper.select:

 select CONTRACT_NO, EMPLOYEE_ID, LABOUR_DATE, LABOUR_TYPE, STANDARD_LABOUR, PM_ID, IS_ADDITIONAL, INVALID_REASON, IS_TRAVEL, RECORD_TIME, OVERTIME_LABOUR,REMARK,LABOUR_STATUS from tbl_labour_recorder where 1=1
and CONTRACT_NO = #{contractNo:VARCHAR}  
and EMPLOYEE_ID = #{employeeId:CHAR}  
and LABOUR_DATE = date_format(#{labourDate:DATE },'%Y-%m-%d')

mapper.update:
update tbl_labour_recorder
STANDARD_LABOUR = #{standardLabour:DECIMAL} , PM_ID = #{pmId:CHAR} , IS_ADDITIONAL = #{isAdditional:CHAR} , INVALID_REASON = #{invalidReason:CHAR} , IS_TRAVEL = #{isTravel:CHAR} , RECORD_TIME = #{recordTime:DATE} , OVERTIME_LABOUR = #{overtimeLabour:DECIMAL} , LABOUR_TYPE = #{labourType:CHAR} , REMARK = #{remark:VARCHAR} , LABOUR_STATUS = #{labourStatus:VARCHAR} ,
and CONTRACT_NO = #{contractNo:VARCHAR} and EMPLOYEE_ID = #{employeeId:CHAR} and LABOUR_DATE = #{labourDate:DATE}

mapper.select:

 select lr.CONTRACT_NO, lr.EMPLOYEE_ID, lr.LABOUR_DATE, lr.LABOUR_TYPE, lr.STANDARD_LABOUR, lr.PM_ID, lr.IS_ADDITIONAL, lr.INVALID_REASON, lr.IS_TRAVEL, lr.RECORD_TIME, lr.OVERTIME_LABOUR,lr.REMARK,LABOUR_STATUS from tbl_labour_recorder lr ,tbl_employee_info tei ,tbl_department_info dept where lr.EMPLOYEE_ID = tei.EMPLOYEE_ID and tei.DEPARTMENT_ID = dept.DEPARTMENT_ID
and lr.CONTRACT_NO = #{contractNo:VARCHAR}  
and lr.EMPLOYEE_ID = #{employeeId:CHAR}  
and lr.LABOUR_DATE = #{labourDate:DATE}  
and lr.LABOUR_TYPE = #{labourType:CHAR}  
and lr.STANDARD_LABOUR = #{standardLabour:DECIMAL}  
and lr.PM_ID = #{pmId:CHAR}  
and lr.IS_ADDITIONAL = #{isAdditional:CHAR}  
and lr.INVALID_REASON = #{invalidReason:CHAR}  
and lr.IS_TRAVEL = #{isTravel:CHAR}  
and lr.RECORD_TIME = #{recordTime:DATE}  
and lr.OVERTIME_LABOUR = #{overtimeLabour:DECIMAL}  
and lr.LABOUR_STATUS = #{labourStatus:VARCHAR}  
and date_format(lr.LABOUR_DATE,'%Y-%m-%d')>=#{properties.exportStatTimeFrom:VARCHAR}  
and date_format(lr.LABOUR_DATE,'%Y-%m-%d')<=#{properties.exportEndTimeFrom:VARCHAR}  
and date_format(lr.LABOUR_DATE,'%Y-%m')>=#{properties.defaultExportStatTimeFrom:VARCHAR}  
and date_format(lr.LABOUR_DATE,'%Y-%m')<=#{properties.defaultExportEndTimeFrom:VARCHAR}  
and tei.BEYOND_COMPANY = #{properties.exportComapyFrom:VARCHAR}  
and tei.DEPARTMENT_ID = #{properties.exportDeptmentFrom:VARCHAR}  
and dept.area = #{properties.exportAreaFrom:VARCHAR}  
and lr.CONTRACT_NO = #{properties.exportContractNoFrom:VARCHAR}  
and tei.EMPLOYEE_NAME = #{properties.exportEmployeeName:VARCHAR}
